fun_falseLoadingBar(){
    # Create a fake loading bar that randomly completes.
    # Yeah, I know its pointless, but it looks cool!
    # Whats the point of writing this awesome script if
    # it can't also look badass while it works?
    local int_percent int_bars int_updateNum str_bar
    int_percent=0
    echo '    :: Loading...'
    echo -ne "::                                                                             :::::::\r"
    while [[ ${int_percent} -lt 100 ]]; do
        int_bars=$( echo "${int_percent} * .72" | bc 2>/dev/null )
        int_bars=$(printf "%.0f" ${int_bars})
        str_bar="$(eval printf '=%.0s' {1..${int_bars}})"
        echo -ne "    ::${str_bar}::(${int_percent}%)\r"
        sleep 1
        int_updateNum=$(( ( RANDOM % 25 )  + 1 ))
        int_percent=$((${int_percent} + ${int_updateNum}))
    done
    str_bar="$(eval printf '=%.0s' {1..72})"
    echo -ne "    ::${str_bar}::(100%)\r"
    echo ''
    sleep 1
}
