fun_fixHostname(){
    # Check that the Hostname is set up correctly.
    if [[ $(hostname -f|grep -o "\."|wc -l) -lt 2 ]] ;then
        local str_hostname str_domainName str_fqdn str_confirm
        echo -e "${color_RED}Host Name is not set up correctly! Fixing...${color_NC}"
        read -r -p 'Input only the Domain Name this Host belongs to: ' str_domainName
        read -r -p 'Input only this servers Hostname, not FQDN: ' str_hostname
        str_fqdn="${str_hostname}.${str_domainName}"
        echo -e "${color_YELLOW}====================================${color_NC}"
        echo -e "HostName: ${color_YELLOW}${str_hostname}${color_NC}"
        echo -e "DomainName: ${color_YELLOW}${str_domainName}${color_NC}"
        echo -e "FQDN: ${color_YELLOW}${str_fqdn}${color_NC}"
        echo ''
        echo -e "${color_YELLOW}Keep in mind, if you are running this in a container${color_NC}"
        echo -e "${color_YELLOW}you need to make sure the container name matches.${color_NC}"
        echo ''
        echo -e "${color_YELLOW}Does the new hostname look correct?${color_NC}"
        echo ''
        read -r -p 'y/N?  :' str_confirm
        if [ "${str_confirm}" = 'y' ]; then
            echo -e "${color_YELLOW}Updating /etc/hosts file...${color_NC}"
            ( echo "127.0.0.1       localhost";
            echo "127.0.1.1  ${str_fqdn}  ${str_hostname}";
            echo "";
            echo "::1    ip6-localhost   ip6-loopback";
            echo "fe00::0    ip6-localnet";
            echo "ff00::0    ip6-mcastprefix";
            echo "ff02::1    ip6-allnodes";
            echo "ff02::2    ip6-allrouters" ) > /etc/hosts
            echo "${str_hostname}" > /etc/hostname
            hostname "${str_hostname}"
            hostnamectl set-hostname "${str_hostname}"
            fun_fixHostname
        else
            fun_fixHostname
        fi
    else
        echo -e "${color_GREEN}Hostname and FQDN set correctly.${color_NC}"  | tee -a -i "${str_logFile}"
    fi
}
