fun_addNewAccount() {
    #
    local str_userName str_domainName str_clearTextWebPW str_clearTextSftpPW str_encryptedPW
    str_userName="$1"
    str_domainName="$2"
    ############################
    # Check if all arguments have been given
    if [ -z "${str_userName}" ] || [ -z "${str_domainName}" ]; then
            echo "Missing arguments"
            echo "correct syntax: ./addWebUserAccount.sh UserName DomainName"
            exit 1
    fi
    ############################
    # Check for repeat username
    if grep "${str_userName}" /etc/passwd ; then
            echo "User already exists!"
            exit 1
    fi
    ############################
    # START User Create and web folder tree.
    str_clearTextWebPW="$(fun_newPasswordGen)"
    str_clearTextSftpPW="$(fun_newPasswordGen)"
    str_encryptedPW="$(perl -e 'print crypt($ARGV[0], "password")' ${str_clearTextSftpPW})"
    (useradd -m -p "${str_encryptedPW}" "${str_userName}") >/dev/null 2>&1
    # usermod -a -G "${str_g_webGroup}" "${str_userName}"
    usermod -a -G "${str_g_sftpGroup}" "${str_userName}"
    usermod -s /bin/false "${str_userName}"
    chown -R root. "/home/${str_userName}" && chmod -R 755 "/home/${str_userName}"
    rm -fr "/home/${str_userName:?}/*"
    touch "/home/${str_userName}/YouCanOnlyUseTheWwwrootFolder.fyi"
    touch "/home/${str_userName}/ToLockAFolderFromPublicVeiwCopyThe-.htaccess-FileIntoTheFolder.fyi"
    mkdir "/home/${str_userName}/wwwroot" && chown -R "${str_userName}:${str_userName}" "/home/${str_userName}/wwwroot" && chmod -R 2755 "/home/${str_userName}/wwwroot"
    mkdir "/home/${str_userName}/logs" && chown -R "${str_userName}." "/home/${str_userName}/logs" && chmod -R 2770 "/home/${str_userName}/logs"
    touch "/home/${str_userName}/logs/${str_domainName}_modsec.log"
    mkdir "/home/${str_userName}/ssl" && chown root. "/home/${str_userName}/ssl" && chmod -R 700 "/home/${str_userName}/ssl"
    echo "It's Works" >> "/home/${str_userName}/wwwroot/index.html"
    ############################
    # Make directory restriction file
    echo "AuthType Basic
    AuthName 'Enter you Username & Password.'
    AuthUserFile /home/${str_userName}/.secret/.htpasswd
    Require valid-user" > "/home/${str_userName}/.htaccess"
    ############################
    ## Add user .htaccess rights to MySQL Web Tool like phpmyadmin or Adminer.
    if [ -f "/home/${str_userName}/.secret/.htpasswd" ] ; then
        (htpasswd -B -C 10 -b "/home/${str_userName}/.secret/.htpasswd" "${str_userName}" "${str_clearTextWebPW}") >/dev/null 2>&1
        (htpasswd -B -C 10 -b "${str_g_apache2Dir}.secret/.htpasswd" "${str_userName}" "${str_clearTextWebPW}") >/dev/null 2>&1
    else
        mkdir "/home/${str_userName}/.secret"
        chmod 755 "/home/${str_userName}/.secret"
        (htpasswd -B -C 10 -c -b "/home/${str_userName}/.secret/.htpasswd" "${str_userName}" "${str_clearTextWebPW}") >/dev/null 2>&1
        (htpasswd -B -C 10 -b "${str_g_apache2Dir}.secret/.htpasswd" "${str_userName}" "${str_clearTextWebPW}") >/dev/null 2>&1
        chmod 644 "/home/${str_userName}/.secret/.htpasswd"
        # May need to add chown to htaccess
    fi
    ############################
    # START  MySQL User Creation
    ##
    local str_rootPasswd int_mysqlPassTrys str_clearTextMysqlPW str_databaseName str_mysqlUserName
    # Create random password
    str_clearTextMysqlPW="$(fun_newPasswordGen)"
    # replace "-" with "_" for database username
    str_databaseName="${str_domainName//[^a-zA-Z0-9]/_}"
    # Limit the Mysql Username
    str_mysqlUserName="${str_userName:0:15}"
    ##
    echo -e "${color_YELLOW}Please enter ROOT user MySQL Password.${color_NC} "
    echo -e "${color_YELLOW}This is needed to set up the new user's MySQL account.${color_NC} "
    read -s -r -p "Enter MySQL password: " str_rootPasswd
    echo ''
    # Number of guesses counter to break endless loops.
    int_mysqlPassTrys=0
    # Make sure the MySQL Password is good.
    while ! mysql -uroot -p"${str_rootPasswd}"  -e ";" ; do
        if [[ ${int_mysqlPassTrys} -ge 9 ]]; then
            echo -e "${color_RED}!!TOO MANY INCORRECT PASSWORD, STOPPING SCRIPT!!${color_NC}"
            exit 1
        else
            echo -e "${color_RED}!!That Root MySQL Password is INCORRECT!!${color_NC}"
            read -s -r -p "Enter Root MySQL password: " str_rootPasswd
            sleep 1
            int_mysqlPassTrys=$(( ${int_mysqlPassTrys} +1 ))
        fi
    done
    #
    if mysql -uroot -p"${str_rootPasswd}"  -e ";" ; then
        mysql -u"${str_g_mysqlRootUser}" -p"${str_rootPasswd}" -e "CREATE DATABASE ${str_databaseName} /*\!40100 DEFAULT CHARACTER SET utf8 */;"
        mysql -u"${str_g_mysqlRootUser}" -p"${str_rootPasswd}" -e "CREATE USER ${str_mysqlUserName}@localhost IDENTIFIED BY '${str_clearTextMysqlPW}';"
        mysql -u"${str_g_mysqlRootUser}" -p"${str_rootPasswd}" -e "GRANT ALL PRIVILEGES ON ${str_databaseName}.* TO '${str_mysqlUserName}'@'localhost';"
    else
        echo -e "${color_RED}!!That Root MySQL Password is INCORRECT!!${color_NC}"
        echo -e "${color_RED}          !!Stopping Script!!${color_NC}"
        exit 1
    fi
    # END MySQL user account creation
    ############################
    # Add Web User Data to file for later use in Backs and removals.
    fun_createWebUserInfoFile "${str_domainName}" "${str_userName}" "${str_mysqlUserName}" "${str_databaseName}"
    ############################
    # Make sure all Apache Modules neeed are enabled.
    fun_configApacheModules
    ############################
    # Generate Self-Signed Cert for website.
    (openssl req -x509 -nodes -days 365 -newkey rsa:2048 \
    -keyout "/home/${str_userName}/ssl/${str_domainName}.key" \
    -out "/home/${str_userName}/ssl/${str_domainName}.crt" \
    -subj "/C=${str_g_country}/ST=${str_g_state}/L=${str_g_locality}/O=${str_domainName}/OU=${str_g_organizationalunit}/CN=${str_domainName}/emailAddress=admin@${str_domainName}") >/dev/null 2>&1
    ############################
    # Start create apache config file with self signed cert. Needed to make good cert
    local str_apacheConfigFile
    str_apacheConfigFile="${str_g_apache2Dir}sites-available/${str_domainName}.conf"
    touch "${str_apacheConfigFile}"
    # Create a Custom 404 Error pages
    echo "<h1 style='color:red'>Error 404: Not found :-(</h1>
    <p>Sorry, but I cannot find that file..  Are you sure you typed in the correct URL?</p>" > "/home/${str_userName}/wwwroot/custom_404.html"
    # Create a Custom 5xx Error pages
    echo "<h1>Bonk! Something went wrong...</h1> > /var/www/html/custom_50x.html
    <p>Hmm.. We seem to be having some technical difficulties. Hang tight.</p>" > "/home/${str_userName}/wwwroot/custom_50x.html"
    ##
    echo "
    # HTTP site Config for ${str_domainName}
    <VirtualHost *:80>
        ServerAdmin admin@${str_domainName}
        ServerName ${str_domainName}
        ServerAlias www.${str_domainName}
        DocumentRoot /home/${str_userName}/wwwroot/
        SecRuleEngine On
        RewriteEngine On
        RewriteCond %{HTTPS} off [OR]
        RewriteCond \"%{HTTP_HOST}\" \"^www\.\" [NC]
        RewriteCond \"%{HTTP_HOST}\" \"!^$\"
        RewriteRule \"^/?(.*)\"      \"https://%{HTTP_HOST}/\$1\" [L,R=301,NE]
        <IfModule mpm_itk_module>
            AssignUserId ${str_userName} ${str_userName}
        </IfModule>
        <Directory /home/${str_userName}/wwwroot/>
            AllowOverride All
            Require all granted
            AAHatName ${str_domainName}-a2
        </Directory>
        LogLevel warn
        SecAuditLog /home/${str_userName}/logs/${str_domainName}_modsec.log
        ErrorLog /home/${str_userName}/logs/${str_domainName}_error.log
        CustomLog /home/${str_userName}/logs/${str_domainName}_access.log combined
    </VirtualHost>
    # HTTPS site Config for ${str_domainName}
    <IfModule mod_ssl.c>
        <VirtualHost _default_:443>
            ServerAdmin admin@${str_domainName}
            ServerName ${str_domainName}
            ServerAlias www.${str_domainName}
            DocumentRoot /home/${str_userName}/wwwroot/
            SecRuleEngine On
            SSLStrictSNIVHostCheck on
            <IfModule mpm_itk_module>
                AssignUserId ${str_userName} ${str_userName}
            </IfModule>
            <Directory /home/${str_userName}/wwwroot/>
                AllowOverride All
                Require all granted
                AAHatName ${str_domainName}-a2
            </Directory>
            # enable HTTP/2, if available
            ProtocolsHonorOrder on
            Protocols h2 h2c http/1.1
            # HTTP Strict Transport Security (mod_headers is required) (63072000 seconds)
            Header always set Strict-Transport-Security \"max-age=63072000\"
            SSLEngine on
            SSLCertificateFile /home/${str_userName}/ssl/${str_domainName}.crt
            SSLCertificateKeyFile /home/${str_userName}/ssl/${str_domainName}.key
            <FilesMatch \"\.(cgi|shtml|phtml|php)$\">
                SSLOptions +StdEnvVars
            </FilesMatch>
            <Directory /usr/lib/cgi-bin>
                SSLOptions +StdEnvVars
            </Directory>
            LogLevel warn
            SecAuditLog /home/${str_userName}/logs/${str_domainName}_modsec.log
            ErrorLog /home/${str_userName}/logs/${str_domainName}_error.log
            CustomLog /home/${str_userName}/logs/${str_domainName}_access.log combined
        </VirtualHost>
    </IfModule>" > "${str_apacheConfigFile}"
    ############################
    # Create the AppArmor Config
    # AppArmor and Apache must be configured and the new site enabled BEFORE we can have LetsEncrypt issue a SSL cert!
    echo "
    ^${str_domainName}-a2 flags=(attach_disconnected){
    #include <abstractions/apache2-common>
    #include <abstractions/base>
    #include <abstractions/php>
    # Allow Read-Write Access to main Web DIR
    /home/${str_userName}/wwwroot/** rw,
    # Allow Read-only access for authenticating .htaccess
    /home/${str_userName}/.secret/.htpasswd r,
    /etc/apache2/.secret/.htpasswd r,
    /var/lib/letsencrypt/http_challenges/** r,
    #  Allow Write-only Access log files
    /home/${str_userName}/logs/${str_domainName}_access.log w,
    /home/${str_userName}/logs/${str_domainName}_error.log w,
    /home/${str_userName}/logs/${str_domainName}_modsec.log w,
    # Allow Read-Write Access to MySQL service
    /run/mysqld/mysqld.sock wr,
    # Deny Access to Data leaking files.
    deny /home/${str_userName}/wwwroot/{Changelog,LICENSE,README,RELEASE-DATE-*,CONTRIBUTING.md,composer.*} r,
    # Deny access to Bash/sh
    deny /bin/bash r,
    deny /bin/sh r,
    }" > "/etc/apparmor.d/apache2.d/${str_domainName}-a2"
    # Parse and Enable the new AppArmor Config.
    fun_priorityCMD "apparmor_parser -r /etc/apparmor.d/usr.sbin.apache2" "Apache's AppArmor profile enforced"
    # Enable the new website config file.
    fun_priorityCMD "a2ensite ""${str_domainName}"".conf >/dev/null 2>&1" "Enable new site"
    ############################
    if [ "${bln_g_letsencrypt_support}" == true ]; then
        # Confirm CertBot is intstalled. The "fun_certbotInstall" funtion must return a code of '0' to ensure CertBot is working corretly.
        local int_certbotInstallExitCode
        fun_certbotInstall
        int_certbotInstallExitCode=$?
        # Tell user we are genorating a new cert.
        echo -e "${color_YELLOW}Certbot now issuing a new SSL Cert...${color_NC}" | tee -a -i "${str_g_logFile}"
        # Create a new SSL cert with Certbot.
        if [ ${int_certbotInstallExitCode} -eq 0 ] && \
        (certbot certonly -n --apache --rsa-key-size 4096 --staple-ocsp --agree-tos --email "admin@${str_domainName}" -d "${str_domainName}"); then
            # The Certbot exited with code 0. Cert creation good.
            echo -e "${color_GREEN}Certbot issue new SSL Cert SUCCESS${color_NC}" | tee -a -i "${str_g_logFile}"
            # Link exiting self-signed cert to new LetsEncrypt Certs
            if (/usr/bin/ln -f -s /etc/letsencrypt/live/"${str_domainName}"/fullchain.pem /home/"${str_userName}"/ssl/"${str_domainName}".crt) && \
               (/usr/bin/ln -f -s /etc/letsencrypt/live/"${str_domainName}"/privkey.pem /home/"${str_userName}"/ssl/"${str_domainName}".key); then
                echo -e "${color_GREEN}Install new SSL Cert SUCCESS${color_NC}" | tee -a -i "${str_g_logFile}"
            else
                echo -e "${color_RED}Install new SSL Cert !FAIL!${color_NC}" | tee -a -i "${str_g_logFile}"
            fi
        else
            # There was an issue with the LetsEncrypt CertBot.
            echo -e "${color_RED}Certbot issue new SSL Cert !FAIL!${color_NC}"  | tee -a -i "${str_g_logFile}"
            # If fail report it and add the LetsEncrypt debug log to the DHL_Install log.
            echo -e "${color_RED}++++++++++++++++++++++++++++++++++++++${color_NC}"  | tee -a -i "${str_g_logFile}"
            echo -e "${color_RED}       START LetsEncrypt Debug Log${color_NC}"  | tee -a -i "${str_g_logFile}"
            echo -e "${color_RED}++++++++++++++++++++++++++++++++++++++${color_NC}"  | tee -a -i "${str_g_logFile}"
            tee -a -i "${str_g_logFile}" < '/var/log/letsencrypt/letsencrypt.log'
            echo -e "${color_RED}++++++++++++++++++++++++++++++++++++++${color_NC}"  | tee -a -i "${str_g_logFile}"
            echo -e "${color_RED}       END LetsEncrypt Debug Log${color_NC}"  | tee -a -i "${str_g_logFile}"
            echo -e "${color_RED}++++++++++++++++++++++++++++++++++++++${color_NC}"  | tee -a -i "${str_g_logFile}"
        fi
    else
        echo -e "${color_YELLOW}Certbot Disabled. Using self-signed cert.${color_NC}"
    fi
    ############################
    # Restart Apache
    fun_priorityCMD "apachectl graceful >/dev/null 2>&1" "Apache service restart"
    ############################
    ##
    echo "====================================================="
    echo "                    SFTP Login"
    echo "    Host:  $(hostname -f)"
    echo "    Port:  ${str_g_port}"
    echo "UserName:  ${str_userName}"
    echo "Password:  ${str_clearTextSftpPW}"
    echo "====================================================="
    echo "                    MySQL Login"
    echo "DatabaseName:  ${str_databaseName}"
    echo "    UserName:  ${str_mysqlUserName}"
    echo "    Password:  ${str_clearTextMysqlPW}"
    echo "====================================================="
    echo "            Htaccess + PhpMyAdmin Login"
    echo "    UserName:  ${str_userName}"
    echo "    Password:  ${str_clearTextWebPW}"
    echo "====================================================="
}
