fun_newPasswordGen() {
    local str_newPw1 str_newPw2 str_newPw3 str_newPw
    str_newPw1="$(openssl rand -base64 15 | tr -dc '[:alnum:]\n\r')"
    if [[ "$(expr length ${str_newPw1})" -lt 15 ]]; then
        str_newPw2="$(openssl rand -base64 15 | tr -dc '[:alnum:]\n\r')"
        str_newPw3="${str_newPw1}${str_newPw2}"
        str_newPw="${str_newPw3:0:15}"
    else
        local str_newPw="${str_newPw1:0:15}"
    fi
    echo "${str_newPw}"
}
