fun_fail2banConfig() {
    # Install and configure Fail2Ban service.
    # Run the install
    fun_priorityCMD "apt-get install -yq fail2ban sendmail sendmail-bin git" "Fail2Ban Install"
    # Fail2Ban Config
    echo -e "[DEFAULT]\ndbpurgeage = 30d" > /etc/fail2ban/fail2ban.local
    # Get the IP address used for WAN access. Needed for the ignoreip config
    str_mainIpv4Addr=$(ip route get 1.1.1.1 | grep -oP 'src \K\S+')
    # Set the Jail Config
    echo "
    [DEFAULT]
    ignoreip = 127.0.0.1/8 ${str_mainIpv4Addr}
    bantime = 4w
    findtime = 10m
    maxretry = 4
    backend = auto
    usedns = warn
    destemail = ${str_g_adminEmail}
    sendername = $(hostname)_Fail2Ban@$(hostname -d)
    banaction = iptables-multiport
    mta = sendmail

    [sshd]
    enabled   = true

    [apache]
    enabled  = true
    port     = http,https
    filter   = apache-auth
    logpath  = /var/log/apache*/*error.log
            /home/*/logs/*error.log

    [apache-overflows]
    enabled  = true
    port     = http,https
    filter   = apache-overflows
    logpath  = /var/log/apache*/*error.log
            /home/*/logs/*error.log

    [apache-badbots]
    enabled  = true
    port     = http,https
    filter   = apache-badbots
    logpath  = /var/log/apache*/*error.log
            /home/*/logs/*error.log

    [apache-nohome]
    enabled  = true
    port     = http,https
    filter   = apache-nohome
    logpath  = /var/log/apache*/*error.log
            /home/*/logs/*error.log

    [php-url-fopen]
    enabled = true
    port    = http,https
    filter  = php-url-fopen
    logpath = /var/log/apache*/*access.log
            /home/*/logs/*access.log
    " > /etc/fail2ban/jail.local
    #
    # Set the correct file permissions.
    chown root:root /etc/fail2ban/*.local
    chmod 644 /etc/fail2ban/*.local
    # Enable SendMail to run at boot and restart the service
    fun_priorityCMD "systemctl enable sendmail && systemctl start sendmail" "SendMail Start Service"
    # Enable Fail2Ban to run at boot and restart the service
    fun_priorityCMD "systemctl enable fail2ban && systemctl restart fail2ban" "Fail2Ban Start Service"
