fun_checkForUpdates() {
    local int_daysSinceLattUpdate str_lastUpdateTimeStamp
    # Check is the system has ever been updated. If updates never run, this file will not exist.
    if [ -f "/var/lib/apt/periodic/update-success-stamp" ] ; then
        # System was updated. Check if system fully up-to-date.
        str_lastUpdateTimeStamp=$(stat -c %Y /var/lib/apt/periodic/update-success-stamp)
        int_daysSinceLattUpdate=$(( ($(date +%s) - $(date +%s -d @"${str_lastUpdateTimeStamp}")) / (60*60*24) ))
        if [[ "${int_daysSinceLattUpdate}" -gt "0" ]]; then
            fun_osUpdateCommands
        else
            echo -e "${color_GREEN}OS Already Up to date..${color_NC}" | tee -a -i "${str_g_logFile}"
        fi
    else
        # System Never updated, running..
        fun_osUpdateCommands
    fi

}
