fun_fullInstall() {

    # Add the start time to the install log
    echo "Start Install Time $(date)" | tee -a -i -- "${str_g_logFile}"

    # Check if install has already run.
    fun_checkInstallerRun

    # Cuz this took a long time to build, and it looks cool.
    fun_cgLogo

    # Check that the Hostname is set up correctly.
    fun_fixHostname

    #Update, Upgrade, and Remove un-needed packages.
    fun_checkForUpdates

    # Generate MySQL Root Password, and Htaccess User with random number on username.
    local str_webAdminUsername str_webAdminPassword str_clearTextMysqlRootPW
    str_clearTextMysqlRootPW="$(fun_newPasswordGen)"
    str_webAdminUsername="admin_$((1000 + $RANDOM % 9999))"
    str_webAdminPassword="$(fun_newPasswordGen)"
    # Send new  MySQL, and HTAccess Username and Password to BaseInstall Fuction.
    # Basic install of LAMP services
    fun_baseInstall "${str_webAdminUsername}" "${str_webAdminPassword}" "${str_clearTextMysqlRootPW}"

    # SFTP Setup
    fun_checkSFTP

    # Install and config Mod_Secure
    fun_modSecure_install

    # Config Apache and secure it.
    fun_apacheSecConfig

    # Set up Automatic updates
    fun_installAutoUpdate

    #  Install and config Fail2Ban
    fun_fail2banConfig

    # AppArmor Install and Config
    fun_apparmorInstall

    # Config Firewall basic settings SSH and Apache Only
    fun_firewallConfig

    # OsSec Install and Config
    fun_ossecInstall

    # Install Let's Encrypt Certbot software. For auto SSL cert managment.
    fun_certbotInstall

    # Configure SSL/TLS settings to be very strong.
    fun_perfectSSL

    # Install and configure ClamAV with Daily scans.
    fun_installClamAV

    # Restart all newly added Services
    # Restart Apache
    fun_priorityCMD "systemctl restart apache2.service" "Apache service restart" 0

    # Restart MariaDB(MySQL)
    fun_priorityCMD "systemctl restart mariadb.service" "MariaDB service restart" 0

    # Check if there was errors during install.
    fun_checkForInstallErrors

    # Finishing flare.
    fun_diamondLampLogo
    echo -e "    +------------------------------------------------------------+"
    echo -e "     The set up is done. Make sure you record your admin details!"
    echo -e "    +------------------------------------------------------------+"

    #  Setup Details
    echo "       |=====================================================|"
    echo "       |================= Install Complete ==================|"
    echo "       |=====================================================|"
    echo "       |                    MySQL Login                      |"
    echo "       |    UserName:  root                                  |"
    echo -en "       |    Password:                                        |\r"
    echo "       |    Password:  ${str_clearTextMysqlRootPW}"
    echo "       |=====================================================|"
    echo "       |             PhpMyAdmin .Htaccess Login              |"
    echo -en "       |   UserName:                                         |\r"
    echo "       |   UserName:  ${str_webAdminUsername}"
    echo -en "       |   Password:                                         |\r"
    echo "       |   Password:  ${str_webAdminPassword}"
    echo "       |=====================================================|"

    # Add the end time to the install log.
    echo "END Install Time $(date)" | tee -a -i -- "${str_g_logFile}"

}
